import ButtonAppointment from '../BtnAppointment/ButtonAppointment';
import './Header.css'



const Header = () => {

    //change the responsive menu icon between menu bar and a x
    //and show or hidde the menu
    const handleChange = (e)=> {
        e.preventDefaul()
        const btnMenuResponsive = document.getElementById('btnMenuResponsive')
        btnMenuResponsive.preventDefaul
        const iconMenu = btnMenuResponsive.style.backgroundImage
        if (iconMenu === 'url("./images/icons/IconBarMenuOpen.svg")') {
            
            btnMenuResponsive.style.backgroundImage = "url('./images/icons/IconBarMenuClose.svg')"
        }else{
            btnMenuResponsive.style.backgroundImage = "url('./images/icons/IconBarMenuOpen.svg')"
        }
        console.log(iconMenu);
    }

    return (
        <header className='header'>
            <div className='whiteDiv' />
            <img id='logoHeader' alt='logo' src='./images/logo.jpg' />
            <div className='nav-container'>

                <ButtonAppointment className='barMenu-btn-Appointment' />

                <a href="#contact">CONTACTO</a>
                <a href="#services">SERVICIOS</a>
                <a href="#home">INICIO</a>

            </div>

            <div className='responsiveMenu'>
                <ButtonAppointment className='barMenu-btn-Appointment'  />
                <button className = 'btnResponsiveMenu' id= 'btnMenuResponsive' style = {{backgroundImage :"url('./images/icons/IconBarMenuOpen.svg')"}} onClick = {(e)=>handleChange(e)} />
        
                <div className='nav-container'>

                    <a href="#contact">CONTACTO</a>
                    <a href="#services">SERVICIOS</a>
                    <a href="#home">INICIO</a>
                </div>


            </div>
        </header>
    )
}

export default Header;
